import React, { useState, useEffect } from 'react';
import axios from 'axios';
import jwtDecode from 'jwt-decode';
import { useNavigate } from 'react-router-dom';


function TaskDashboard() {
  const navigate = useNavigate(); 
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/');
      return;
    }
    try {
      const decodedToken = jwtDecode(token);
      const userId = decodedToken.userId;
      axios
        .get(`http://localhost:3006/users/${userId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then((response) => {
          setUser(response.data.user);
        })
    } catch (error) {
      setError('Invalid token. Please log in again.');
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userId');
    navigate('/login');
  };

  return (
    <div className="max-w-screen-xl mx-auto p-4">
      {user && (
        <div className="bg-white rounded-lg shadow-lg p-4">
          <h2 className="text-2xl font-bold mb-4">User Profile</h2>
          <div className="flex items-center">
            <div className="w-30 h-30 ml-4 rounded-full overflow-hidden">
              <img
                src={`http://localhost:3006/${user.profilePicture}`}
                alt={`Product: ${user.name}`}
                className="w-full h-full object-cover"
              />
            </div>
            <div className="ml-10 text-xl container px-4 max-w-md  ">
              <p className="mb-4 mt-6">
                <strong>User ID:</strong> {user._id}
              </p>
              <p className="mb-4">
                <strong>Name:</strong> {user.username}
              </p>
            </div>
          </div>
          <button
            onClick={handleLogout}
            className="mt-4 bg-red-500 hover:bg-red-600 text-white font-semibold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
            Logout
          </button>
        </div>
      )}
    </div>
  );
}

export default TaskDashboard;
