import React, { useState } from 'react';
import axios from 'axios';
import jwtDecode from 'jwt-decode';

function AddTask() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState('');
  const [successMessage, setSuccessMessage] = useState(''); // State for success message

  const handleAddTask = () => {
    const token = localStorage.getItem('token');
    const decodedToken = jwtDecode(token);
    const userId = decodedToken.userId;

    // Send a POST request to create the task
    axios
      .post(`http://localhost:3006/task/${userId}`, {
        title,
        description,
        status,
        createdBy: userId,
      })
      .then((response) => {
        // Handle success, e.g., clear the input fields and set the success message
        setTitle('');
        setDescription('');
        setStatus('');
        setSuccessMessage('Task added successfully.');
      })
      .catch((error) => {
        // Handle errors, e.g., display an error message
        console.error('Error adding the task:', error);
      });
  };

  return (
    <div className="max-w-screen-xl mx-auto p-4 h-80">
      <div className="bg-white rounded-lg shadow-lg p-4">
        <h2 className="text-2xl font-bold mb-4">Add Task</h2>
        <form>
          <div className="mb-4">
            <label className="font-bold">Title:</label>
            <input
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              className="w-full py-2 px-4 border border-gray-300 rounded focus:outline-none"
              required
            />
          </div>
          <div className="mb-4">
            <label className="font-bold">Description:</label>
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full py-2 px-4 border border-gray-300 rounded focus:outline-none"
            />
          </div>
          <div className="mb-4">
            <label className="font-bold">Status:</label>
            <input
              type="text"
              value={status}
              onChange={(e) => setStatus(e.target.value)}
              className="w-full py-2 px-4 border border-gray-300 rounded focus:outline-none"
            />
          </div>
          <button
            type="button"
            onClick={handleAddTask}
            className="bg-gray-900 text-white py-2 px-4 rounded font-bold cursor-pointer"
          >
            Add Task
          </button>
        </form>
        {successMessage && (
          <div className="text-green-600 mt-4">{successMessage}</div>
        )}
      </div>
    </div>
  );
}

export default AddTask;
