import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import jwtDecode from 'jwt-decode';


function Login() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleLogin = async (e) => {
    try {
      e.preventDefault();
      const response = await axios.post('http://localhost:3005/users/login', { email, password });
      const { message, token } = response.data;
      
      const decodedToken = jwtDecode(token);
      const userId = decodedToken.userId;

      localStorage.setItem('userId', userId);
      localStorage.setItem('token', token);
      setMessage(message);
      navigate('/task');
    } catch (error) {
      setMessage(error.response.data.error);
    }
  };

  const handleSignupClick = () => {
    navigate('/signup');
  };

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const decodedToken = jwtDecode(token);
      const userId = decodedToken.userId;
      localStorage.setItem('userId', userId);
    }
  }, []);
;

return (
    <div className="flex">
        <div className="w-1/2">
            <img
            src="./image/Login.jpeg" 
            alt="Background Image"
            className="w-full h-screen object-cover"
            />
        </div>
        <div className="w-1/2 pt-20  bg-sky-500 ">
            <div className="flex flex-col items-center max-w-xs mx-auto p-4 mt-8  rounded border-black ">
                <h2 className="text-2xl font-bold mb-4">Login</h2>
                <form>
                    <div className="mb-4">
                    <label className="font-bold">Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        className="w-full py-2 px-4 border border-gray-300 rounded focus:outline-none"
                        required
                    />
                    </div>
                    <div className="mb-4">
                    <label className="font-bold">Password:</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        className="w-full py-2 px-4 border border-gray-300 rounded focus:outline-none"
                        required
                    />
                    </div>
                    <button
                    type="submit"
                    onClick={handleLogin}
                    className="bg-gray-900 text-white py-2 px-4 rounded font-bold cursor-pointer"
                    >
                    Login
                    </button>
                    <p className="mt-2 text-sky-500">
                    </p>
                </form>
                {message && <p>{message}</p>}
            </div>
            <div className="bg-gray-900 w-96 h-1 mt-4 mb-4"></div>
            <div class="mt-6 ">
                <button
                onClick={handleSignupClick}
                className="bg-gray-900 text-white py-2 px-4 rounded font-bold cursor-pointer mt-2"
                >
                Create Your Account
                </button>
            </div>
        </div>
    </div>
  );
}

export default Login;
