import React, { useState, useEffect } from 'react';
import axios from 'axios';
import jwtDecode from 'jwt-decode';
import AddTask from './AddTask';

import { useNavigate } from 'react-router-dom';

function TaskDashboard() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    try {
      const decodedToken = jwtDecode(token);
      const userId = decodedToken.userId;

      axios
        .get(`http://localhost:3006/users/${userId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then((response) => {
          setUser(response.data.user);
        })
        .catch((error) => {
          setError('Error fetching user details. Please try again.');
        });

      axios
        .get(`http://localhost:3006/task/${userId}`)
        .then((response) => {
          setTasks(response.data.tasks);
        })
        .catch((error) => {
          if (error.response && error.response.status === 404) {
            setError('No tasks found for this user.');
          } else {
            setError('An error occurred while fetching tasks.');
          }
        })
        .finally(() => {
          setLoading(false);
        });
    } catch (error) {
      setError('Invalid token. Please log in again.');
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userId');
    navigate('/');
  };

  const handleDeleteTask = (taskId) => {
    axios
      .delete(`http://localhost:3006/tasks/${taskId}`)
      .then(() => {
        // Remove the deleted task from the tasks list
        setTasks((prevTasks) => prevTasks.filter((task) => task._id !== taskId));
      })
      .catch((error) => {
        // Handle errors, e.g., display an error message
        console.error('Error deleting the task:', error);
      });
  };

  return (
    <div className="flex ">
      <div className="w-1/4 h-screen border-2">
        {user && (
          <div className="p-4 ">
            <h2 className="text-2xl font-bold mb-4">User Profile</h2>
            <div className="flex flex-col items-center">
              <div className="w-30 h-30 mb-4 rounded-full overflow-hidden">
                <img
                  src={`http://localhost:3006/${user.profilePicture}`}
                  alt={`Profile Picture: ${user.username}`}
                  className="w-full h-full object-cover"
                />
              </div>
              <div className="text-xl">
                <p className="mb-4">
                  <strong>User ID:</strong> {user._id}
                </p>
                <p className="mb-4">
                  <strong>Name:</strong> {user.username}
                </p>
              </div>
            </div>
            <button
              onClick={handleLogout}
              className="mt-4 bg-red-500 hover-bg-red-600 text-white font-semibold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            >
              Logout
            </button>
          </div>
        )}
      </div>
      <div className="w-3/4">
        <div className="">
          <AddTask />
        </div>
        <div className="bg-gray-900 w-full h-1 mt-4 mb-4"></div>
        <div className="p-4">
          <h2 className="text-2xl font-bold mb-4">Your Task List</h2>
          {loading && <p>Loading...</p>}
          {error && <p className="text-red-500">{error}</p>}
          {tasks.length === 0 && !loading && !error && <p>No tasks found for this user.</p>}
          {tasks.length > 0 && (
            <table className="w-full">
              <thead className="bg-blue-700 text-white">
                <tr>
                  <th className="py-2 text-center border">Task ID</th>
                  <th className="py-2 text-center border">Task Name</th>
                  <th className="py-2 text-center border">Status</th>
                  <th className="py-2 text-center border">Actions</th>
                </tr>
              </thead>
              <tbody>
                {tasks.map((task) => (
                  <tr key={task._id}>
                    <td className="border py-2">{task._id}</td>
                    <td className="border py-2">{task.title}</td>
                    <td className="border py-2">{task.status}</td>
                    <td className="border py-2">
                      <button className="bg-blue-500 hover:bg-blue-600 text-white py-1 px-2 rounded mx-1">
                        Edit
                      </button>
                      <button className="bg-red-500 hover:bg-red-600 text-white py-1 px-2 rounded mx-1"
                        onClick={() => handleDeleteTask(task._id)}>
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </div>
  );
}

export default TaskDashboard;
