import React, { useState, useEffect } from 'react';
import axios from 'axios';
import jwtDecode from 'jwt-decode';
import { useNavigate } from 'react-router-dom';

function TaskDashboard() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    try {
      const decodedToken = jwtDecode(token);
      const userId = decodedToken.userId;

      axios
        .get(`http://localhost:3006/users/${userId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then((response) => {
          setUser(response.data.user);
        })
        .catch((error) => {
          setError('Error fetching user details. Please try again.');
        });

      axios
        .get(`http://localhost:3006/task/${userId}`)
        .then((response) => {
          setTasks(response.data.tasks);
        })
        .catch((error) => {
          if (error.response && error.response.status === 404) {
            setError('No tasks found for this user.');
          } else {
            setError('An error occurred while fetching tasks.');
          }
        })
        .finally(() => {
          setLoading(false);
        });
    } catch (error) {
      setError('Invalid token. Please log in again.');
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userId');
    navigate('/login');
  };

  return (
    <div className="flex ">
      <div className="w-1/4 ">
        {user && (
          <div className="bg-white rounded-lg shadow-lg p-4">
            <h2 className="text-2xl font-bold mb-4">User Profile</h2>
            <div className="flex flex-col items-center">
              <div className="w-30 h-30 mb-4 rounded-full overflow-hidden">
                <img
                  src={`http://localhost:3006/${user.profilePicture}`}
                  alt={`Profile Picture: ${user.username}`}
                  className="w-full h-full object-cover"
                />
              </div>
              <div className="text-xl">
                <p className="mb-4">
                  <strong>User ID:</strong> {user._id}
                </p>
                <p className="mb-4">
                  <strong>Name:</strong> {user.username}
                </p>
              </div>
            </div>
            <button
              onClick={handleLogout}
              className="mt-4 bg-red-500 hover-bg-red-600 text-white font-semibold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            >
              Logout
            </button>
          </div>
        )}
      </div>
      <div className="w-3/4">
        <div className="bg-white rounded-lg shadow-lg p-4">
          <h2 className="text-2xl font-bold mb-4">Your Task List</h2>
          {loading && <p>Loading...</p>}
          {error && <p className="text-red-500">{error}</p>}
          {tasks.length === 0 && !loading && !error && <p>No tasks found for this user.</p>}
          {tasks.length > 0 && (
            <ul>
              {tasks.map((task) => (
                <li key={task._id} className="mb-2">
                  {task.title}
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
}

export default TaskDashboard;
